import React, { Fragment, Component } from 'react'
import { Button, Dialog, DialogActions, DialogContent, Select, MenuItem, FormControl, InputLabel, TextField, DialogContentText, DialogTitle, Fab  } from '@material-ui/core'
import AddIcon from '@material-ui/icons/Add';

export default class extends Component {
  state = {
    open: false,
    exercise: {
      title: '',
      description: '',
      muscles:''
    }
  }

  handleToggle = () => {
    this.setState({
      open: !this.state.open
    })
  }

  handleChange = name => ({ target: { value } }) => {
    this.setState ({
      exercise: {
        ...this.state.exercise,
        [name]: value
      }
    })
  }

  render() {
    const { open, exercise: { title, description, muscles } } = this.state,
            { muscles: categories } = this.props
    
    return <Fragment>
      <Fab
        color="initial"
        onClick={this.handleToggle}
        size="small"
        aria-label="Add"
      >
        <AddIcon />
      </Fab>

      <Dialog
        open={open}
        onClose={this.handleClose}
      >
        <DialogTitle id="form-dialog-title">Create a new exercise</DialogTitle>
        <DialogContent>
          <DialogContentText>
            Please fill out the form blow.
          </DialogContentText>
        </DialogContent>
        <form style={{textAlign: 'center'}}>
          <TextField
            label="Title"
            value={title}
            onChange={this.handleChange('title')}
            margin="normal"
          />
        <br/>
        <FormControl>
          <InputLabel htmlFor="muscles">
            Muscles
          </InputLabel>
          <Select
            value={muscles}
            onChange={this.handleChange('muscles')}
          >
          {categories.map(category =>
            <MenuItem value={category}>
              {category}
            </MenuItem>
          )}
          </Select>
        </FormControl>
        <br />
          <TextField
            label="Description"
            value={description}
            multiline
            id="a2"
            rows="4"
            onChange={this.handleChange('description')}
            margin="normal"
          />

        </form>
        <DialogActions>
          <Button color="primary" variant="contained">
            Create
          </Button>
        </DialogActions>
      </Dialog>
    </Fragment>
  }
}
