import React, { Fragment } from 'react'
import { Grid, Paper, Typography, List, ListItem, ListItemText } from '@material-ui/core';

const styles = {
  Paper: { padding: 20, marginTop: 10, marginBottom: 10, height: 400, overflowY: 'auto' }
}

export default ({
  exercises,
  category,
  onSelect,
  exercise: {id, title = 'Welcome!', description = 'Lorem ipsum dolor sit amet, consectetuer adipiscing elit!'}
  }) =>
  <Grid container sm={12}>
    <Grid item sm={3}>
      <Paper style={styles.Paper}>
        {exercises.map(([group, exercises]) =>
          !category || category === group
          ? <Fragment key={group}>
              <Typography variant="h6" style={{textTransform: 'capitalize'}}>
                {group}
              </Typography>

              <List component="ul" aria-label="Secondary mailbox folders">
                {exercises.map(({ id, title }) =>
                  <ListItem
                    key={id}
                    button
                    onClick={() => onSelect(id)}
                  >
                    <ListItemText
                      primary={title}
                    />
                  </ListItem>                   
                )}
              </List>
            </Fragment>
          : null
        )}
      </Paper>
    </Grid>
    <Grid item sm={9}>
      <Paper style={styles.Paper}>
        <Typography variant="h4">
          {title}
        </Typography>
        <Typography variant="subtitle2" style={{marginTop: 20}}>
          {description}
        </Typography>
      </Paper>
    </Grid>
  </Grid>
